mapper.select:
SELECT *
FROM MEMBER
WHERE id = #{id}
  AND pw = #{pw}
  AND QUIT_STATUS = 0

mapper.update:
UPDATE MEMBER
SET NAME = #{name},
    PW = #{pw},
    PHONE = #{phone},
    BIRTH = #{birth}
WHERE ID = #{id}

mapper.delete:
UPDATE MEMBER
SET QUIT_STATUS = 1,
    POINT = 0
WHERE ID = #{id}
    AND PW = #{pw}

mapper.select:
SELECT ID
FROM MEMBER
WHERE PHONE = #{phone}

mapper.update:
UPDATE MEMBER
SET POINT = POINT + #{point}
WHERE ID = #{memberId}
